startTimeStamp :: TimeSeries a ->  TimeStamp
startTimeStamp  x  = 1


endTimeStamp :: TimeSeries a ->  TimeStamp
endTimeStamp  x  = 490



autoTSeries :: TimeStamp -> TimeSeries a -> TimeSeries a 
autoTSeries n xs = \t -> (xs (t-n))



-- Using a Dummy last time Stamp, Need to do something at later stage. 
getLastTimeStamp :: TimeSeries a -> TimeStamp
getLastTimeStamp x = 50


getTimeSerieslist :: TimeSeries a ->(TimeStamp,TimeStamp) -> [(TimeStamp,a)]
getTimeSerieslist x (t1,t2)
                     | t1==t2     = [(t1, (x t1))]
                     | otherwise  = (getTimeSerieslist x (t1,t2-1))++[(t2,(x t2))]


convertTimeSeriesTuple :: [(TimeStamp,Price)] -> [(Int,Float)]
convertTimeSeriesTuple []         = []
convertTimeSeriesTuple ((t1,v1):xs) = [(t1,v1)]++(convertTimeSeriesTuple xs)



timeSPlot :: TimeSeries Price -> TimeStamp -> [(TimeStamp,Price)]
timeSPlot ab 0 = []
timeSPlot ab t =  (timeSPlot ab (_updateInc t 1)) ++[(t,(ab t))] 

tSplot :: TimeSeries Price -> TimeStamp  -> [(Int,Float)]
tSplot  ab t = convertTimeSeriesTuple (timeSPlot ab t)




